window.getVeServices = function getVeServices(veName) {
  if (!veName) return [];
  const map = (window.MockReleaseMapping && window.MockReleaseMapping.ExpectedServices) || {};
  return Array.isArray(map[veName]) ? map[veName].slice() : [];
};






